/* * Robot: Alvo *  * Permanece imóvel no campo de batalha. Útil como alvo para testes. */// Importa o modulo de portabilidade#include "port_module.h"using namespace std;class Alvo : public Robot {	public:		// Enquanto true o robô continua a funcionar		Alvo();		~Alvo();		void start();			private:		bool alive;};Alvo::Alvo() {	alive = true;	// Inicia o robô	init();}Alvo::~Alvo() {}void Alvo::start() {	while ( alive ) {		// Para não ser retirado da batalha por inactividade		noop();		sleep(2);	}}int main() {	Alvo robot;	robot.start();	return 0;}