/* * Robot: Paredes *  * Percorre o campo de batalha encostado às paredes no sentido dos ponteiros do relógio. */#include "port_module.h"class Paredes : public Robot {	public:		Paredes();		~Paredes();		void start();		private:		// Enquanto true o robô continua a funcionar		bool alive;		void round_started( int args );		void out_of_energy();};Paredes::Paredes() {	alive = true;	// Inicia o robô	init();}Paredes::~Paredes(){}void Paredes::round_started( int args ) {	stringstream ss;	ss << args;	cout << "round_started( " + ss.str() +  " )" << endl;	subscribe_events( "[out_of_energy]" );}void Paredes::out_of_energy() {	sleep( 1 );	set_speed( 2 );}void Paredes::start() {	int direction = 0;	while ( alive ) {		robot_move_forward( 1000 );		robot_turn_to( direction );		direction += 90;		if ( direction == 360 )			direction = 0;	}}int main() {	Paredes robot;	robot.start();	return 0;}